    def pay_1(self):

        kolvo = 1
        
        mycursor.execute(f'INSERT INTO `basket` (`title`, `kolvo`, `price`) SELECT `title`, "{kolvo}",`price` FROM `warehouse` WHERE `id` = 1')
        
        result = mycursor.fetchall()
        mydb.commit()

        print("+")

