    def pay(self):
        
        mycursor.execute("DELETE FROM basket")
        mydb.commit()

        print("Paay!")

    def lable_1(self):
        
        mycursor.execute("SELECT title FROM warehouse WHERE id = 1")
        result = mycursor.fetchone()
        
        if result:
            title = result[0]
            self.label.setText(title)
        
        mycursor.execute("SELECT title FROM warehouse WHERE id = 2")
        result = mycursor.fetchone()
        
